



// HIDING FOOTER AREA
export const hideFooter = () => {
	const footerDom = document.getElementById("footer_wrapper")
	footerDom.style.display = 'none'
}




// SHOWING FOOTER AREA
export const showFooter = () => {
	const footerDom = document.getElementById("footer_wrapper")
	footerDom.style.display = ''
}



